//Bringing in our top level function provided by express framework
let express = require("express");
//Creating the app object
let app = express();
let PORT = 8080;
//for any request where the route is /hello, send a response that is a string: hello
app.get("/hello", function (req,res){
    res.send("hello");
})
//for any request where the route is /hello/: followed by a name
//return hello followed by the specefied name
app.get("/hello/:name",function (req,res){
    let name = req.params.name;
    res.send(`hello ${name}`)
})
//request url = /bye?name=rob => "see ya later rob"
//request url = /bye => "see ya later"
//hint: you get query params using request.query.name
app.get("/bye", function (req,res){
    let goodbye = req.query.name
    if (goodbye === undefined){
        res.send(`see ya later`)
    } else {
    res.send(`see ya later ${goodbye}`)}
})


app.listen(PORT, function (){
    console.log("Application has started and is listening on port ", PORT);
})